import React,{useEffect, useState} from 'react';
import { useHistory } from "react-router-dom";
import {useDispatch, useSelector} from 'react-redux';
import {fetchAllUsers,setUser} from '../../redux/reducers/slices/users'; 

function NavAvatar() {

    const fullname = "Timi Ambaye";
    const avatar = "/logo192.png"; 

    const dispatch = useDispatch();
    const history = useHistory(); 

    const user = useSelector(state => state.users.user);

    const [currentUser, setCurrentUser] = useState({});

    useEffect(()=>{
        if(user?.id){
            setCurrentUser(user)
        }
    },[user])

    const logout =()=>{

    }

    return (
        <div className="flex w-2/5 items-end justify-end p-2">
            <span>
                {currentUser?.name ? `Hello ${currentUser.name}` : "Select user account" }
            </span>

            {currentUser?.avatarURL 
                ? <img 
                    src={currentUser.avatarURL} 
                    alt="User avatar" 
                    className="w-6 h-6 ml-2 mr-2 rounded-full bg-gray-500" 
                />
                : <img 
                    src={avatar} 
                    alt="User avatar" 
                    className="w-6 h-6 ml-2 mr-2 rounded-full bg-gray-500" 
                />
            }

            {
                currentUser?.name 
                    ? <button 
                        className="font-extrabold"
                        onClick={logout}
                    >
                        Logout
                    </button>
                    : <button 
                        className="font-extrabold"
                        onClick={()=>history.push("/")}
                    >
                        Login
                    </button>

            }

        </div>
    )
}

export default NavAvatar
